Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Bieg bokserski',CONCAT('1) Pozycja stojąca, tułów wyprostowany, stopy w lekkim rozkroku.'),CONCAT('1) Wykonuj bieg w miejscu (skipping) połączony z naprzemiennymi wyprostami ramion w przód (ciosami prostymi).',CHAR(10),'2) Kiedy wykonujesz cios prawą ręką, lewa noga jest ugięta, a kolano znajduje się wysoko z przodu (udo niemal równolegle do podłoża).'),CONCAT('1) Szybki oddech podczas wykonywania biegu.',CHAR(10),'2) Bieg bokserski wykonuj dynamicznie, silnie prostuj ramiona w przód oraz podnoś wysoko kolana.'),2,'bieg_bokserski.mp4',4, '/domowe/Bieg_bokserski.jpg');
