Insert into exercises (name,description_of_start_position,description_of_correct_execution, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Martwy ciąg na jednej nodze',CONCAT('1) Pozycja stojąca, stopy wąsko obok siebie, ramiona wyprostowane wzdłuż tułowia.',CHAR(10),'2) Plecy proste. Nogi minimalnie ugięte w kolanach (odblokowane w stawie), jedna minimalnie uniesiona.'),CONCAT('1) Utrzymując prawidłową pozycję ciała, wykonaj wdech, następnie wysuwając biodra w tył, pochylaj ciało do przodu i unoś jedną nogę. ',CHAR(10),'2) Kontynuuj ruch do momentu wyczucia rozciągania w okolicach mięśni dwugłowych ud.',CHAR(10),'3) Powróć do pozycji wyjściowej, prostując tułów i wykonując wydech.',CHAR(10),'4) Wykonaj wyznaczoną liczbę powtórzeń, po czym powtórz ćwiczenie na drugą nogę.'),1,'martwy_ciag_na_jednej_nodze.mp4',4, '/domowe/Martwy_ciag_na_jednej_nodze.jpg');
