Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Burpees',CONCAT('1) Pozycja stojąca, tułów wyprostowany.'),CONCAT('1) Ugnij nogi w kolanach i przejdź do podporu przodem, po czym odrzuć nogi w tył, wyprostuj sylwetkę, dzięki czemu uzyskasz pozycję jak do pompki.',CHAR(10),'2) Wykonaj klasyczną pompkę i ponownie „przeskocz” nogami w przód, tak aby kolana znalazły się pod klatką piersiową.',CHAR(10),'3) Wstań i wykonaj wyskok, ramiona powinny być wyprostowane nad głową.',CHAR(10),'4) Powtórz punkt 1–3 wyznaczoną liczbę powtórzeń.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Wykonuj pompkę tak, aby klatka piersiowa dotknęła podłoża.',CHAR(10),'3) Bądź skoncentrowany.',CHAR(10),'4) Napinając mięśnie brzucha oraz pośladkowe, zachowuj prawidłową (wyprostowaną) postawę.',CHAR(10),'5) Pamiętaj o prawidłowym oddychaniu.',CHAR(10),'6) Przyciągnięcie kolan do klatki oraz oddalenie od niej następuje dynamicznie.',CHAR(10),'7) Staraj się wykonać jak najwyższy wyskok.'),1,'burpees.mp4',4, '/domowe/Burpees.jpg');
