Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Przyciąganie pięt do pośladków w leżeniu na plecach',CONCAT('1) Pozycja leżąca, nogi wyprostowane w kolanach, pięty oparte o ręcznik, kawałek materiału lub specjalne dyski do slide`ów.',CHAR(10),'2) Ramiona wyprostowane spoczywają wzdłuż tułowia.'),CONCAT('1) Wykonaj wdech. Utrzymując prawidłową pozycję, unieś lekko biodra, ugnij jednocześnie kończyny dolne, próbując przyciągnąć pięty do pośladków.',CHAR(10),'2) Postaraj się utrzymać maksymalne napięcie mięśni dwugłowych ud, gdy nogi są ugięte w kolanach.',CHAR(10),'3) Następnie wykonując wydech, wolno wróć do pozycji wyjściowej, kontroluj ruch.',CHAR(10),'4) Wykonaj ruch wyznaczoną liczbę powtórzeń.'),CONCAT('1) Wykonuj ruch w wolnym tempie.',CHAR(10),'2) Bądź skoncentrowany.',CHAR(10),'3) Pamiętaj o prawidłowym oddychaniu.',CHAR(10),'4) Postaraj się znaleźć właściwe napięcie mięśniowe.'),1,'przyciaganie_piet_do_posladkow_w_lezeniu_na_plecach.mp4',4, '/domowe/Przyciaganie_piet_do_posladkow_w_lezeniu_na_plecach.jpg');
