Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wchodzenie na podwyższenie z uniesieniem kolana',CONCAT('1) Ustaw podest przed sobą. Możesz wykorzystać ławkę, krzesło, stolik bądź inny rodzaj solidnego podestu.',CHAR(10),'2) Oddal się od podestu na odległość 2 długości stóp.'),CONCAT('1) Z pozycji wyprostowanej, stojąc naprzeciwko podestu, weź wdech, postaw swoją lewą stopę na podest, po czym drugą nogą wykonaj lekkie odbicie i wejdź na podwyższenie. ',CHAR(10),'2) Wykonaj pełny wyprost w stawach kolanowym i biodrowym, noga odbita z podłogi nie dotyka pudła, unosimy ją wysoko, ugiętą w kolanie. ',CHAR(10),'3) Po uniesieniu kolana powróć do pozycji wyjściowej, cofnij i opuść prawą nogę, schodząc z podestu.',CHAR(10),'4) Wejdź na podest nogą przeciwną, wykonaj powtórzenie na nogę przeciwną.'),CONCAT('1) Dobierz wysokość, na jaką będziesz wchodzić, według indywidualnych możliwości.',CHAR(10),'2) Skup się podczas wykonywania ruchu.',CHAR(10),'3) Pamiętaj o prawidłowym oddychaniu.',CHAR(10),'4) Utrzymuj napięte mięśnie brzucha podczas fazy wchodzenia na podest.',CHAR(10),'5) Postaraj się nie pochylać głowy i klatki piersiowej.'),1,'wchodzenie_na_podwyzszenie_z_uniesieniem_kolana.mp4',4, '/domowe/Wchodzenie_na_podwyzszenie_z_uniesieniem_kolana.jpg');
