Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Przysiad z wyskokiem w górę',CONCAT('1) Pozycja stojąca, plecy wyprostowane, ramiona skierowane w dół. ',CHAR(10),'2) Mięśnie brzucha oraz pośladki napięte, łopatki ściągnięte.',CHAR(10),'3) Stopy mniej więcej na szerokości barków (palce stóp lekko na zewnątrz).'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, weź wdech, wykonaj płynny i powolny przysiad (utrzymując naturalną krzywiznę kręgosłupa) do pozycji poniżej kąta prostego, ramiona wysuń przed siebie. ',CHAR(10),'2) Z wydechem wykonaj dynamiczny wyprost kolan wraz z wyskokiem w górę i dynamiczną pracą ramion. Opadając, amortyzuj skok, uginając kolana oraz schodząc równocześnie do pozycji przysiadu.',CHAR(10),'3) Wykonaj ruch wyznaczoną liczbę powtórzeń.'),CONCAT('1) Pamiętaj o utrzymaniu prostych pleców.',CHAR(10),'2) Głowę utrzymuj w równej linii z plecami.',CHAR(10),'3) Wzrok kieruj przed siebie (ew. lekko do góry).',CHAR(10),'4) Bądź skoncentrowany.',CHAR(10),'5) Nie prowadź kolan do wewnątrz (utrzymuj je skierowane lekko na zewnątrz).',CHAR(10),'6) Ciężar rozłóż równomiernie na całych stopach.'),1,'przysiad_z_wyskokiem_w_gore.mp4',4, '/domowe/Przysiad_z_wyskokiem_w_gore.jpg');
