Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Superman',CONCAT('1) Połóż się na brzuchu, nogi wyprostowane, palce stóp skierowane za siebie.',CHAR(10),'2) Ramiona wyprostowane przed sobą.',CHAR(10),'3) Głowa znajduje się w jednej linii z tułowiem, wzrok skierowany w dół.'),CONCAT('1) Weź wdech, utrzymując prawidłową pozycję wyjściową, napnij mocno mięśnie nóg, pośladki, brzuch, unieś nogi oraz ramiona kilkanaście centymetrów w górę.',CHAR(10),'2) Ramiona oraz nogi pozostają wyprostowane, głowa stanowi przedłużenie kręgosłupa, wytrzymaj w tej pozycji kilka sekund.',CHAR(10),'3) Wraz z wydechem powietrza obniż nogi oraz ramiona do pozycji wyjściowej.'),CONCAT('1) Mięśnie brzucha powinny być mocno napięte przez cały czas trwania ćwiczenia.',CHAR(10),'2) Dolny odcinek pleców nie powinien być nadmiernie wygięty w łuk.'),1,'superman.mp4',3, '/domowe/Superman.jpg');
