Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wspięcia na palce stojąc',CONCAT('1) Stań prosto.',CHAR(10),'2) Ustaw przednią część stopy na podwyższeniu, np. na stopniu schodów.'),CONCAT('1) Z pozycji, w której stopa jest mocno zadarta do góry, pięta skrajnie obniżona, palce skierowane w górę, a łydka mocno rozciągnięta, odepchnij się od podwyższenia poprzez mocne wspięcie na palce i napięcie łydek.',CHAR(10),'2) Po krótkim przytrzymaniu w maksymalnym napięciu łydki opuść pięty do poziomu palców, ponownie rozciągnij łydkę i wykonaj wydech.',CHAR(10),'3) Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Przednia część podeszwy stopy stabilnie spoczywa na podwyższeniu.',CHAR(10),'2) Bądź skoncentrowany.'),1,'wspiecia_na_palce_stojac.mp4',5, '/domowe/Wspiecia_na_palce_stojac.jpg');
